   const arr = tasks.filter(task=>{
       if(selected!=="All"){
        return task["category"]===selected}
        else{
        return tasks
        }}

    )
   return  <TaskList tasks={arr} updateTasks={updateTasks}/>

}
    render(){
        // const {selected,tasks} = this.props
