import React from 'react';
import './App.css';
import { CATEGORIES } from './data'
import CategoryFilter from './components/CategoryFilter'
import Tasks from './components/Tasks.js'
class App extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ],
    selected:"All"
  }

  selectedCategory=(category)=>{
    this.setState({
      selected:category
    })
  }
  render() {
    return (
      <div className="App">
        <h2>My tasks</h2>
        <h5>Category filters</h5>
        <CategoryFilter tasks={this.state.tasks} categories={CATEGORIES} selected={this.selectedCategory}/>
        <Tasks selected={this.state.selected} tasks={this.state.tasks}/>
      </div>
    );
  }
}


export default App;
